mapper.select:
SELECT * FROM learning_resource 
WHERE 1=1

    AND resource_name LIKE CONCAT('%', #{resourceName}, '%')


    AND resource_type = #{resourceType}


    AND category_id = #{categoryId}


    AND difficulty_level = #{difficultyLevel}


    AND status = #{status}

ORDER BY create_time DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM learning_resource 
WHERE 1=1

    AND resource_name LIKE CONCAT('%', #{resourceName}, '%')


    AND resource_type = #{resourceType}


    AND category_id = #{categoryId}


    AND difficulty_level = #{difficultyLevel}


    AND status = #{status}

mapper.update:
UPDATE learning_resource 
SET rating = #{rating}, rating_count = #{ratingCount}
WHERE resource_id = #{resourceId}
